        cid: response.data.IpfsHash,
        url: result.url,
        metadata: metadata,
        createdAt: new Date().toISOString()
      };

      // Save to local storage
      saveStoredMetadata(storedMetadata);

      return result;
    } else {
      throw new Error('Invalid response from Pinata');
    }
  } catch (error: any) {
    console.error('Error storing metadata on Pinata:', error);

    // Enhanced error handling
    if (error.response) {
      // The request was made and the server responded with a status code
      // that falls out of the range of 2xx
      const statusCode = error.response.status;
      const responseData = error.response.data;

      if (statusCode === 401) {
        throw new Error('Authentication failed. Please check your Pinata API keys or JWT token.');
      } else if (statusCode === 429) {
        throw new Error('Rate limit exceeded. Please try again later.');
      } else if (responseData && responseData.error) {
        throw new Error(responseData.error);
      } else {
        throw new Error(`Server error: ${statusCode}`);
      }
    } else if (error.request) {
      // The request was made but no response was received
      throw new Error('No response from Pinata. Please check your internet connection.');
    } else {
      // Something happened in setting up the request that triggered an Error
      throw error;
    }
  }
};
