import React, { useState, useEffect } from 'react';
import { NFTMetadata, UploadedImage } from '../types/nft';
import ImageUploader from './ImageUploader';

interface MetadataFormProps {
  onMetadataStored: (tokenURI: string) => void;
}

const MetadataForm: React.FC<MetadataFormProps> = ({ onMetadataStored }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [uploadedImage, setUploadedImage] = useState<UploadedImage | null>(null);
  const [manualCID, setManualCID] = useState('');
  const [attributes, setAttributes] = useState<Array<{ trait_type: string; value: string }>>([
    { trait_type: '', value: '' }
  ]);
  const [isStoring, setIsStoring] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Pre-fill the CID from your successful Pinata upload
  useEffect(() => {
    setManualCID('bafkreidllrnxdnv3wuabiezzhykhuimigdrmqbhhqtzyf2c36tnsvzp34a');
  }, []);

  const handleImageUploaded = (image: UploadedImage) => {
    setUploadedImage(image);
  };

  const handleManualCIDSubmit = () => {
    if (!manualCID.trim()) {
      setError('Please enter a valid CID');
      return;
    }

    // Create the image object
    const image: UploadedImage = {
      cid: manualCID,
      url: `https://gateway.pinata.cloud/ipfs/${manualCID}`,
      name: 'Manual Upload',
      size: 0
    };

    setUploadedImage(image);
  };

  const handleAddAttribute = () => {
    setAttributes([...attributes, { trait_type: '', value: '' }]);
  };

  const handleRemoveAttribute = (index: number) => {
    const newAttributes = [...attributes];
    newAttributes.splice(index, 1);
    setAttributes(newAttributes);
  };

  const handleAttributeChange = (index: number, field: 'trait_type' | 'value', value: string) => {
    const newAttributes = [...attributes];
    newAttributes[index][field] = value;
    setAttributes(newAttributes);
  };

  // Direct metadata storage function that bypasses the service
  const storeMetadataDirectly = async (metadata: NFTMetadata) => {
    const API_KEY = "4f5eb1e973348c67f7db";
    const API_SECRET = "76c752db760ae75231ea6e7c2729da9f9318c44671c46b292c64e5855c4d9884";

    console.log("Storing metadata directly:", metadata);

    // Prepare the JSON to be pinned
    const data = {
      pinataOptions: {
        cidVersion: 1
      },
      pinataMetadata: {
        name: `${metadata.name}-metadata`,
        keyvalues: {
          type: 'nft-metadata',
          timestamp: Date.now().toString()
        }
      },
      pinataContent: metadata
    };

    const response = await fetch('https://api.pinata.cloud/pinning/pinJSONToIPFS', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'pinata_api_key': API_KEY,
        'pinata_secret_api_key': API_SECRET
      },
      body: JSON.stringify(data)
    });

    if (!response.ok) {
      const errorText = await response.text();
      console.error("Pinata error response:", errorText);
      throw new Error(`Pinata API error: ${response.status} ${response.statusText}`);
    }

    const result = await response.json();
    console.log("Metadata stored successfully:", result);

    return {
      url: `https://gateway.pinata.cloud/ipfs/${result.IpfsHash}`,
      ipfsHash: result.IpfsHash
    };
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);

    // Validation
    if (!name.trim()) {
      setError('Name is required');
      return;
    }

    if (!description.trim()) {
      setError('Description is required');
      return;
    }

    if (!uploadedImage) {
      setError('Image is required');
      return;
    }

    // Filter out empty attributes
    const filteredAttributes = attributes.filter(
      (attr) => attr.trait_type.trim() && attr.value.trim()
    );

    // Create metadata object
    const metadata: NFTMetadata = {
      name,
      description,
      image: uploadedImage.url,
      attributes: filteredAttributes
    };

    setIsStoring(true);

    try {
      // Use direct storage instead of service function
      const storedMetadata = await storeMetadataDirectly(metadata);

      // Call callback with token URI
      onMetadataStored(storedMetadata.url);

      // Reset form
      setName('');
      setDescription('');
      setUploadedImage(null);
      setAttributes([{ trait_type: '', value: '' }]);
    } catch (error) {
      console.error('Error storing metadata:', error);
      setError(`Failed to store metadata: ${error instanceof Error ? error.message : 'Unknown error'}`);
    } finally {
      setIsStoring(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="space-y-4">
        <div className="border-t pt-4">
          <h3 className="text-md font-medium mb-3">Use your existing IPFS CID</h3>
          <div className="flex gap-2">
            <input 
              type="text"
              value={manualCID}
              onChange={(e) => setManualCID(e.target.value)}
              placeholder="Enter IPFS CID"
              className="flex-1 rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
            />
            <button
              type="button"
              onClick={handleManualCIDSubmit}
              className="px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700"
            >
              Use CID
            </button>
          </div>
          <p className="text-xs text-gray-500 mt-1">You can use the uploaded CID: bafkreidllrnxdnv3wuabiezzhykhuimigdrmqbhhqtzyf2c36tnsvzp34a</p>
        </div>

        <div className="border-t pt-4">
          <h3 className="text-md font-medium mb-3">Or upload a new image</h3>
          <ImageUploader onImageUploaded={handleImageUploaded} />
        </div>
      </div>

      {uploadedImage && (
        <div className="mt-2">
          <p className="text-sm text-gray-500">Image uploaded successfully:</p>
          <div className="mt-2 flex items-center">
            <div className="mr-4 flex-shrink-0 h-16 w-16 border border-gray-200 rounded-md overflow-hidden">
              <img src={uploadedImage.url} alt="Uploaded" className="h-full w-full object-cover" />
            </div>
            <div>
              <p className="text-sm font-medium text-gray-900">{uploadedImage.name}</p>
              {uploadedImage.size > 0 && (
                <p className="text-sm text-gray-500">
                  {(uploadedImage.size / 1024).toFixed(2)} KB
                </p>
              )}
            </div>
          </div>
        </div>
      )}

      <div>
        <label htmlFor="name" className="block text-sm font-medium text-gray-700">
          Name
        </label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
          placeholder="My NFT #1"
        />
      </div>

      <div>
        <label htmlFor="description" className="block text-sm font-medium text-gray-700">
          Description
        </label>
        <textarea
          id="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          rows={3}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
          placeholder="Description of your NFT"
        />
      </div>

      <div>
        <div className="flex justify-between">
          <label className="block text-sm font-medium text-gray-700">Attributes</label>
          <button
            type="button"
            onClick={handleAddAttribute}
            className="inline-flex items-center px-2.5 py-1.5 border border-transparent text-xs font-medium rounded text-indigo-700 bg-indigo-100 hover:bg-indigo-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
          >
            Add Attribute
          </button>
        </div>

        <div className="mt-2 space-y-3">
          {attributes.map((attribute, index) => (
            <div key={index} className="flex space-x-3">
              <div className="flex-1">
                <input
                  type="text"
                  value={attribute.trait_type}
                  onChange={(e) => handleAttributeChange(index, 'trait_type', e.target.value)}
                  className="block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                  placeholder="Trait Type (e.g. Color)"
                />
              </div>
              <div className="flex-1">
                <input
                  type="text"
                  value={attribute.value}
                  onChange={(e) => handleAttributeChange(index, 'value', e.target.value)}
                  className="block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                  placeholder="Value (e.g. Blue)"
                />
              </div>
              {attributes.length > 1 && (
                <button
                  type="button"
                  onClick={() => handleRemoveAttribute(index)}
                  className="inline-flex items-center p-1.5 border border-transparent rounded-full text-red-600 hover:bg-red-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    className="h-5 w-5"
                    viewBox="0 0 20 20"
                    fill="currentColor"
                  >
                    <path
                      fillRule="evenodd"
                      d="M10 18a8 8 0 100-16 8 8 0 000 16zM7 9a1 1 0 000 2h6a1 1 0 100-2H7z"
                      clipRule="evenodd"
                    />
                  </svg>
                </button>
              )}
            </div>
          ))}
        </div>
      </div>

      {error && (
        <div className="rounded-md bg-red-50 p-4">
          <div className="flex">
            <div className="ml-3">
              <h3 className="text-sm font-medium text-red-800">Error</h3>
              <div className="mt-2 text-sm text-red-700">
                <p>{error}</p>
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="flex justify-end">
        <button
          type="submit"
          disabled={isStoring}
          className="inline-flex justify-center py-2 px-4 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {isStoring ? 'Storing Metadata...' : 'Store Metadata'}
        </button>
      </div>
    </form>
  );
};

export default MetadataForm;
