import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { MyNFTAbi } from '../abis/MyNFTabi';

interface NFTViewerProps {
  contractAddress?: string;
  tokenId?: number;
}

interface NFTMetadata {
  name?: string;
  description?: string;
  image?: string;
  attributes?: Array<{
    trait_type: string;
    value: string | number;
  }>;
  [key: string]: any;
}

const NFTViewer: React.FC<NFTViewerProps> = ({ contractAddress: initialContractAddress, tokenId: initialTokenId }) => {
  const [contractAddress, setContractAddress] = useState<string>(initialContractAddress || '');
  const [tokenId, setTokenId] = useState<string>(initialTokenId?.toString() || '');
  const [metadata, setMetadata] = useState<NFTMetadata | null>(null);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [imageUrl, setImageUrl] = useState<string>('');

  // Function to fetch NFT metadata
  const fetchNFTMetadata = async () => {
    if (!contractAddress || !tokenId) {
      setError('Please provide both contract address and token ID');
      return;
    }

    setLoading(true);
    setError(null);
    setMetadata(null);
    setImageUrl('');

    try {
      // Connect to the provider (use window.ethereum if available, otherwise use a public provider)
      const provider = window.ethereum 
        ? new ethers.providers.Web3Provider(window.ethereum)
        : new ethers.providers.JsonRpcProvider('https://rpc.ankr.com/eth');

      // Create contract instance
      const nftContract = new ethers.Contract(contractAddress, MyNFTAbi, provider);

      // Get token URI
      const tokenURI = await nftContract.tokenURI(tokenId);
      console.log('Token URI:', tokenURI);

      // Fetch metadata from the URI
      let metadataJson: NFTMetadata;

      if (tokenURI.startsWith('ipfs://')) {
        // Convert IPFS URI to HTTPS using a gateway
        const ipfsHash = tokenURI.replace('ipfs://', '');
        const metadataUrl = `https://ipfs.io/ipfs/${ipfsHash}`;
        const response = await fetch(metadataUrl);
        metadataJson = await response.json();
      } else if (tokenURI.startsWith('http')) {
        // Regular HTTP URL
        const response = await fetch(tokenURI);
        metadataJson = await response.json();
      } else {
        // If it's base64 encoded or other format, try to parse it
        try {
          const decoded = JSON.parse(tokenURI);
          metadataJson = decoded;
        } catch (e) {
          console.error("Could not parse token URI as JSON:", e);
          metadataJson = { 
            name: "Unknown NFT", 
            description: "Could not parse metadata from the token URI",
            raw_uri: tokenURI
          };
        }
      }

      setMetadata(metadataJson);

      // Process the image URL
      if (metadataJson.image) {
        if (metadataJson.image.startsWith('ipfs://')) {
          const ipfsHash = metadataJson.image.replace('ipfs://', '');
          setImageUrl(`https://ipfs.io/ipfs/${ipfsHash}`);
        } else {
          setImageUrl(metadataJson.image);
        }
      }

    } catch (err) {
      console.error('Error fetching NFT metadata:', err);
      setError('Failed to fetch NFT metadata. Please check the contract address and token ID.');
    } finally {
      setLoading(false);
    }
  };

  // Auto fetch if both contract address and token ID are provided on component mount
  useEffect(() => {
    if (initialContractAddress && initialTokenId !== undefined) {
      fetchNFTMetadata();
    }
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [initialContractAddress, initialTokenId]);

  return (
    <div className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div className="md:col-span-2">
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700">Contract Address</label>
            <input
              type="text"
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
              value={contractAddress}
              onChange={(e) => setContractAddress(e.target.value)}
              placeholder="Enter the NFT contract address"
            />
          </div>
        </div>

        <div>
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700">Token ID</label>
            <input
              type="text"
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
              value={tokenId}
              onChange={(e) => setTokenId(e.target.value)}
              placeholder="Enter the token ID"
            />
          </div>
        </div>
      </div>

      <button
        onClick={fetchNFTMetadata}
        disabled={loading}
        className="inline-flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:bg-indigo-400"
      >
        {loading ? 'Loading...' : 'View NFT'}
      </button>

      {error && (
        <div className="p-4 bg-red-100 border border-red-400 text-red-700 rounded">
          {error}
        </div>
      )}

      {metadata && (
        <div className="bg-white shadow rounded-lg overflow-hidden">
          <div className="p-4 sm:p-6">
            <h3 className="text-xl font-semibold text-gray-900 mb-2">
              {metadata.name || 'Untitled NFT'}
            </h3>

            {imageUrl && (
              <div className="mb-6 flex justify-center">
                <img 
                  src={imageUrl} 
                  alt={metadata.name || 'NFT'} 
                  className="rounded-lg max-h-96 object-contain border"
                  onError={() => setImageUrl('/placeholder-image.png')}
                />
              </div>
            )}

            {metadata.description && (
              <div className="mb-4">
                <h4 className="text-sm font-medium text-gray-700 mb-1">Description</h4>
                <p className="text-gray-600">{metadata.description}</p>
              </div>
            )}

            {metadata.attributes && metadata.attributes.length > 0 && (
              <div>
                <h4 className="text-sm font-medium text-gray-700 mb-2">Attributes</h4>
                <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
                  {metadata.attributes.map((attr, index) => (
                    <div key={index} className="p-2 bg-gray-50 rounded border border-gray-200">
                      <div className="text-xs text-gray-500">{attr.trait_type}</div>
                      <div className="font-medium">{attr.value}</div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          <div className="border-t border-gray-200 px-4 py-3 sm:px-6 bg-gray-50">
            <div className="text-xs text-gray-500">
              Contract: {contractAddress.substring(0, 6)}...{contractAddress.substring(contractAddress.length - 4)}
            </div>
            <div className="text-xs text-gray-500">
              Token ID: {tokenId}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default NFTViewer;
