import React, { useState, useEffect } from 'react';

const PinataSettings: React.FC = () => {
  const [apiKey, setApiKey] = useState('');
  const [apiSecret, setApiSecret] = useState('');
  const [jwt, setJwt] = useState('');
  const [saveStatus, setSaveStatus] = useState<'idle' | 'success' | 'error'>('idle');
  const [statusMessage, setStatusMessage] = useState('');
  const [useJwt, setUseJwt] = useState(false);

  // Load saved credentials on component mount
  useEffect(() => {
    const savedApiKey = localStorage.getItem('pinataApiKey') || '';
    const savedApiSecret = localStorage.getItem('pinataApiSecret') || '';
    const savedJwt = localStorage.getItem('pinataJWT') || '';

    setApiKey(savedApiKey);
    setApiSecret(savedApiSecret);
    setJwt(savedJwt);
    setUseJwt(!!savedJwt);
  }, []);

  const saveSettings = () => {
    try {
      if (useJwt) {
        // If using JWT, save JWT and clear API key/secret
        if (!jwt.trim()) {
          throw new Error('JWT is required when using JWT authentication');
        }
        localStorage.setItem('pinataJWT', jwt.trim());
        localStorage.removeItem('pinataApiKey');
        localStorage.removeItem('pinataApiSecret');
      } else {
        // If using API key/secret, save them and clear JWT
        if (!apiKey.trim() || !apiSecret.trim()) {
          throw new Error('API Key and Secret are required when using API key authentication');
        }
        localStorage.setItem('pinataApiKey', apiKey.trim());
        localStorage.setItem('pinataApiSecret', apiSecret.trim());
        localStorage.removeItem('pinataJWT');
      }

      setSaveStatus('success');
      setStatusMessage('Settings saved successfully');

      // Reset status after 3 seconds
      setTimeout(() => {
        setSaveStatus('idle');
        setStatusMessage('');
      }, 3000);
    } catch (error) {
      setSaveStatus('error');
      setStatusMessage(error instanceof Error ? error.message : 'Failed to save settings');

      // Reset status after 3 seconds
      setTimeout(() => {
        setSaveStatus('idle');
        setStatusMessage('');
      }, 3000);
    }
  };

  // Helper function to extract error message from various error types
  const getErrorMessage = (error: any): string => {
    if (typeof error === 'string') return error;
    if (error instanceof Error) return error.message;
    if (error && typeof error === 'object') {
      if ('message' in error) return String(error.message);
      if ('error' in error) return String(error.error);
      return JSON.stringify(error); // Last resort - convert object to string
    }
    return 'Unknown error occurred';
  };

  const testConnection = async () => {
    try {
      setSaveStatus('idle');
      setStatusMessage('Testing connection...');

      // Prepare headers based on authentication method
      let headers: Record<string, string> = {};
      if (useJwt) {
        if (!jwt.trim()) {
          throw new Error('JWT is required');
        }
        headers = {
          Authorization: `Bearer ${jwt.trim()}`
        };
      } else {
        if (!apiKey.trim() || !apiSecret.trim()) {
          throw new Error('API Key and Secret are required');
        }
        headers = {
          pinata_api_key: apiKey.trim(),
          pinata_secret_api_key: apiSecret.trim()
        };
      }

      // Use the simpler test authentication endpoint first
      const testUrl = 'https://api.pinata.cloud/data/testAuthentication';
      console.log('Testing basic authentication at:', testUrl);

      let testResponse;
      try {
        testResponse = await fetch(testUrl, {
          method: 'GET',
          headers
        });

        if (!testResponse.ok) {
          const errorData = await testResponse.json().catch(() => ({}));
          throw new Error(errorData.error || `Authentication failed with status ${testResponse.status}`);
        }

        console.log('Basic authentication successful, now testing pin list access...');
      } catch (authError) {
        console.error('Authentication test failed:', authError);
        throw new Error(`Authentication failed: ${getErrorMessage(authError)}`);
      }

      // Now test the pin list endpoint
      const response = await fetch('https://api.pinata.cloud/data/pinList?status=pinned&pageLimit=1', {
        method: 'GET',
        headers
      });

      console.log('Pin List response status:', response.status);

      if (!response.ok) {
        let errorMessage = `HTTP error ${response.status}`;
        try {
          const errorData = await response.json();
          errorMessage = errorData.error || errorMessage;
        } catch (e) {
          // If we can't parse the JSON, just use the HTTP status
        }
        throw new Error(errorMessage);
      }

      // If we get here, the connection was successful
      setSaveStatus('success');
      setStatusMessage('Connection successful! Your Pinata credentials are working.');

      // Save settings if test was successful
      saveSettings();

      // Reset status after 3 seconds
      setTimeout(() => {
        setSaveStatus('idle');
        setStatusMessage('');
      }, 3000);
    } catch (error) {
      console.error('Pinata connection test failed:', error);

      // Extract meaningful error message using our helper function
      const errorMessage = getErrorMessage(error);

      setSaveStatus('error');
      setStatusMessage(`Connection failed: ${errorMessage}`);

      // Reset status after 5 seconds
      setTimeout(() => {
        setSaveStatus('idle');
        setStatusMessage('');
      }, 5000);
    }
  };

  const clearSettings = () => {
    localStorage.removeItem('pinataApiKey');
    localStorage.removeItem('pinataApiSecret');
    localStorage.removeItem('pinataJWT');

    setApiKey('');
    setApiSecret('');
    setJwt('');

    setSaveStatus('success');
    setStatusMessage('Settings cleared');

    // Reset status after 3 seconds
    setTimeout(() => {
      setSaveStatus('idle');
      setStatusMessage('');
    }, 3000);
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow mb-8">
      <h2 className="text-xl font-medium text-gray-900 mb-4">Pinata IPFS Settings</h2>
      <p className="text-gray-600 mb-6">
        Connect your Pinata account to store NFT images and metadata on IPFS.
        <a 
          href="https://app.pinata.cloud/signin" 
          target="_blank" 
          rel="noopener noreferrer"
          className="ml-2 text-indigo-600 hover:text-indigo-500"
        >
          Don't have an account? Sign up
        </a>
      </p>

      <div className="mb-4">
        <div className="flex items-center">
          <input
            id="use-api-key"
            type="radio"
            checked={!useJwt}
            onChange={() => setUseJwt(false)}
            className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300"
          />
          <label htmlFor="use-api-key" className="ml-2 block text-sm font-medium text-gray-700">
            API Key Authentication
          </label>
        </div>
        <div className="flex items-center mt-2">
          <input
            id="use-jwt"
            type="radio"
            checked={useJwt}
            onChange={() => setUseJwt(true)}
            className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300"
          />
          <label htmlFor="use-jwt" className="ml-2 block text-sm font-medium text-gray-700">
            JWT Authentication (Recommended)
          </label>
        </div>
      </div>

      {!useJwt ? (
        <div className="space-y-4">
          <div>
            <label htmlFor="apiKey" className="block text-sm font-medium text-gray-700">
              API Key
            </label>
            <input
              type="text"
              id="apiKey"
              value={apiKey}
              onChange={(e) => setApiKey(e.target.value)}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
              placeholder="Enter your Pinata API Key"
            />
          </div>

          <div>
            <label htmlFor="apiSecret" className="block text-sm font-medium text-gray-700">
              API Secret
            </label>
            <input
              type="password"
              id="apiSecret"
              value={apiSecret}
              onChange={(e) => setApiSecret(e.target.value)}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
              placeholder="Enter your Pinata API Secret"
            />
          </div>
        </div>
      ) : (
        <div>
          <label htmlFor="jwt" className="block text-sm font-medium text-gray-700">
            JWT
          </label>
          <input
            type="password"
            id="jwt"
            value={jwt}
            onChange={(e) => setJwt(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
            placeholder="Enter your Pinata JWT"
          />
        </div>
      )}

      {statusMessage && (
        <div className={`mt-4 p-3 rounded ${
          saveStatus === 'success' 
            ? 'bg-green-50 text-green-800' 
            : saveStatus === 'error' 
              ? 'bg-red-50 text-red-800'
              : 'bg-blue-50 text-blue-800'
        }`}>
          {statusMessage}
        </div>
      )}

      <div className="mt-6 flex flex-wrap gap-3">
        <button
          type="button"
          onClick={saveSettings}
          className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
        >
          Save Settings
        </button>
        <button
          type="button"
          onClick={testConnection}
          className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
        >
          Test Connection
        </button>
        <button
          type="button"
          onClick={clearSettings}
          className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md shadow-sm text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
        >
          Clear Settings
        </button>
      </div>
    </div>
  );
};

export default PinataSettings;
