import React, { useState } from 'react';
import { Tab } from '@headlessui/react';
import NFTContractForm from '../components/NFTContractForm';
import MetadataForm from '../components/MetadataForm';
import NFTMinter from '../components/NFTMinter';
import MetadataViewer from '../components/MetadataViewer';
import PinataSettings from '../components/PinataSettings';

const NFTStudio: React.FC = () => {
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [contractAddress, setContractAddress] = useState<string>('');
  const [metadataUri, setMetadataUri] = useState<string>('');

  // This function will be passed to the MetadataForm component
  const handleMetadataStored = (uri: string) => {
    console.log('Metadata stored at:', uri);
    setMetadataUri(uri);

    // Optionally move to the next tab (NFT Minter) if that's your workflow
    setSelectedIndex(2); // Assuming the NFT Minter is the 3rd tab (index 2)
  };

  // This function will be called when a contract is deployed
  const handleContractDeployed = (address: string) => {
    console.log('Contract deployed at:', address);
    setContractAddress(address);

    // Move to the next tab (Metadata)
    setSelectedIndex(1);
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-center mb-8">NFT Studio</h1>

      <Tab.Group selectedIndex={selectedIndex} onChange={setSelectedIndex}>
        <Tab.List className="flex space-x-1 rounded-xl bg-blue-900/20 p-1 mb-8">
          <Tab 
            className={({ selected }) =>
              `w-full rounded-lg py-2.5 text-sm font-medium leading-5 
              ${selected ? 'bg-white text-blue-700 shadow' : 'text-blue-100 hover:bg-white/[0.12] hover:text-white'}`
            }
          >
            1. Create Contract
          </Tab>
          <Tab 
            className={({ selected }) =>
              `w-full rounded-lg py-2.5 text-sm font-medium leading-5 
              ${selected ? 'bg-white text-blue-700 shadow' : 'text-blue-100 hover:bg-white/[0.12] hover:text-white'}`
            }
            disabled={!contractAddress}
          >
            2. Create Metadata
          </Tab>
          <Tab 
            className={({ selected }) =>
              `w-full rounded-lg py-2.5 text-sm font-medium leading-5 
              ${selected ? 'bg-white text-blue-700 shadow' : 'text-blue-100 hover:bg-white/[0.12] hover:text-white'}`
            }
            disabled={!metadataUri}
          >
            3. Mint NFT
          </Tab>
          <Tab 
            className={({ selected }) =>
              `w-full rounded-lg py-2.5 text-sm font-medium leading-5 
              ${selected ? 'bg-white text-blue-700 shadow' : 'text-blue-100 hover:bg-white/[0.12] hover:text-white'}`
            }
          >
            4. Metadata Viewer
          </Tab>
          <Tab 
            className={({ selected }) =>
              `w-full rounded-lg py-2.5 text-sm font-medium leading-5 
              ${selected ? 'bg-white text-blue-700 shadow' : 'text-blue-100 hover:bg-white/[0.12] hover:text-white'}`
            }
          >
            5. Settings
          </Tab>
        </Tab.List>

        <Tab.Panels className="mt-2 bg-white p-6 rounded-xl shadow-md">
          {/* Contract Creation Panel */}
          <Tab.Panel>
            <h2 className="text-2xl font-semibold mb-4">Create NFT Contract</h2>
            <p className="text-gray-600 mb-6">
              Deploy a new ERC-721 NFT contract to the blockchain
            </p>
            <NFTContractForm onContractDeployed={handleContractDeployed} />
          </Tab.Panel>

          {/* Metadata Creation Panel */}
          <Tab.Panel>
            <h2 className="text-2xl font-semibold mb-4">Create NFT Metadata</h2>
            <p className="text-gray-600 mb-6">
              Upload an image and create metadata for your NFT
            </p>
            <MetadataForm onMetadataStored={handleMetadataStored} />
          </Tab.Panel>

          {/* Mint NFT Panel */}
          <Tab.Panel>
            <h2 className="text-2xl font-semibold mb-4">Mint NFT</h2>
            <p className="text-gray-600 mb-6">
              Mint your NFT to the deployed contract
            </p>
            <NFTMinter 
              contractAddress={contractAddress} 
              metadataUri={metadataUri} 
            />
          </Tab.Panel>

          {/* Metadata Viewer Panel */}
          <Tab.Panel>
            <h2 className="text-2xl font-semibold mb-4">Metadata Viewer</h2>
            <p className="text-gray-600 mb-6">
              View and verify your NFT metadata
            </p>
            <MetadataViewer />
          </Tab.Panel>

          {/* Settings Panel */}
          <Tab.Panel>
            <h2 className="text-2xl font-semibold mb-4">Pinata Settings</h2>
            <p className="text-gray-600 mb-6">
              Configure your IPFS storage settings
            </p>
            <PinataSettings />
          </Tab.Panel>
        </Tab.Panels>
      </Tab.Group>
    </div>
  );
};

export default NFTStudio;
