import React, { useState } from 'react';
import { Tab } from '@headlessui/react';
import NFTContractForm from '../components/NFTContractForm';
import MetadataForm from '../components/MetadataForm';
import NFTMinter from '../components/NFTMinter';
import MetadataViewer from '../components/MetadataViewer';
import PinataSettings from '../components/PinataSettings';
import { MyNFTAbi } from '../abis/MyNFTabi';
import SimpleUploader from '../components/SimpleUploader';
import NFTViewer from '../components/NFTViewer';

// We'll use this for internal reference but not directly in the component
// eslint-disable-next-line @typescript-eslint/no-unused-vars
const exampleContractFormat = {
  name: "My NFT Collection",
  address: "0x5f2A098d1A2F39c3119bFa1d4B250ccCf10597B1",
  abi: MyNFTAbi,
  network: "Kairos Testnet"
};

function classNames(...classes: string[]): string {
  return classes.filter(Boolean).join(' ');
}

const NFTStudio = () => {
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [tokenURI, setTokenURI] = useState<string | null>(null);
  const [showSettings, setShowSettings] = useState(true);

  // Add state for tracking minted NFTs
  const [mintedNFTs, setMintedNFTs] = useState<Array<{
    contractAddress: string;
    tokenId: number;
    tokenURI: string;
  }>>([]);

  // Add state for the currently viewed NFT
  const [viewingNFT, setViewingNFT] = useState<{
    contractAddress: string;
    tokenId: number;
  } | null>(null);

  const handleMetadataStored = (uri: string): void => {
    setTokenURI(uri);
    setSelectedIndex(2); // Switch to the Minting tab
  };

  const handleContractSaved = (): void => {
    // After contract is saved, switch to metadata tab if no token URI yet
    if (!tokenURI) {
      setSelectedIndex(1);
    }
  };

  // Add handler for when an NFT is minted
  const handleNFTMinted = (contractAddress: string, tokenId: number): void => {
    // Add the newly minted NFT to our list
    if (tokenURI) {
      setMintedNFTs(prev => [
        ...prev, 
        { contractAddress, tokenId, tokenURI: tokenURI }
      ]);

      // Set this as the currently viewed NFT
      setViewingNFT({ contractAddress, tokenId });

      // Switch to the NFT Viewer tab
      setSelectedIndex(4);
    }
  };

  const tabs = [
    { name: 'Contracts', current: selectedIndex === 0 },
    { name: 'Create Metadata', current: selectedIndex === 1 },
    { name: 'Mint NFT', current: selectedIndex === 2 },
    { name: 'Metadata Library', current: selectedIndex === 3 },
    { name: 'View My NFTs', current: selectedIndex === 4 },
  ];

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-10">
      <div className="mb-8">
        <h1 className="text-2xl font-bold text-gray-900">NFT Studio</h1>
        <p className="mt-2 text-gray-600">
          Create, store, and mint NFTs with ease
        </p>
      </div>

      {/* Toggle for settings */}
      <div className="mb-6">
        <button 
          onClick={() => setShowSettings(!showSettings)}
          className="text-indigo-600 hover:text-indigo-800 font-medium"
        >
          {showSettings ? 'Hide API Settings' : 'Show API Settings'}
        </button>
      </div>

      {/* Conditionally show Pinata Settings */}
      {showSettings && <PinataSettings />}

      <div className="w-full">
        <Tab.Group selectedIndex={selectedIndex} onChange={setSelectedIndex}>
          <Tab.List className="flex space-x-1 rounded-xl bg-blue-900/20 p-1">
            {tabs.map((tab) => (
              <Tab
                key={tab.name}
                className={({ selected }) =>
                  classNames(
                    'w-full rounded-lg py-2.5 text-sm font-medium leading-5',
                    'ring-white ring-opacity-60 ring-offset-2 ring-offset-blue-400 focus:outline-none focus:ring-2',
                    selected
                      ? 'bg-white text-blue-700 shadow'
                      : 'text-blue-100 hover:bg-white/[0.12] hover:text-white'
                  )
                }
              >
                {tab.name}
              </Tab>
            ))}
          </Tab.List>
          <Tab.Panels className="mt-6">
            {/* Contracts Panel */}
            <Tab.Panel className="rounded-xl bg-white p-6 shadow">
              <h2 className="text-xl font-medium text-gray-900 mb-4">
                NFT Contracts
              </h2>
              <p className="text-gray-600 mb-6">
                Add your NFT smart contract details here. You'll need the contract address and ABI.
              </p>
              <NFTContractForm onContractSaved={handleContractSaved} />
            </Tab.Panel>

            {/* Create Metadata Panel */}
            <Tab.Panel className="rounded-xl bg-white p-6 shadow">
              <h2 className="text-xl font-medium text-gray-900 mb-4">
                Create NFT Metadata
              </h2>
              <p className="text-gray-600 mb-6">
                Upload an image and create metadata for your NFT
              </p>
              <MetadataForm onMetadataStored={handleMetadataStored} />
            </Tab.Panel>

            {/* Mint NFT Panel */}
            <Tab.Panel className="rounded-xl bg-white p-6 shadow">
              <h2 className="text-xl font-medium text-gray-900 mb-4">
                Mint NFT
              </h2>
              <p className="text-gray-600 mb-6">
                Mint your NFT using the metadata you created
              </p>
              {tokenURI ? (
                <div>
                  {/* Add an onMinted callback prop to NFTMinter */}
                  <NFTMinter 
                    tokenURI={tokenURI} 
                    onMinted={handleNFTMinted} 
                  />
                </div>
              ) : (
                <div className="text-center p-8 border border-dashed border-gray-300 rounded-lg">
                  <p className="text-gray-500">
                    No metadata created yet. Please go to the "Create Metadata" tab first.
                  </p>
                  <button
                    onClick={() => setSelectedIndex(1)}
                    className="mt-4 inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700"
                  >
                    Create Metadata
                  </button>
                </div>
              )}
            </Tab.Panel>

            {/* Metadata Library Panel */}
            <Tab.Panel className="rounded-xl bg-white p-6 shadow">
              <h2 className="text-xl font-medium text-gray-900 mb-4">
                Metadata Library
              </h2>
              <p className="text-gray-600 mb-6">
                View and manage all your stored metadata
              </p>
              <MetadataViewer />
            </Tab.Panel>

            {/* View My NFTs Panel */}
            <Tab.Panel className="rounded-xl bg-white p-6 shadow">
              <h2 className="text-xl font-medium text-gray-900 mb-4">
                My NFTs
              </h2>
              <p className="text-gray-600 mb-6">
                View your minted NFTs
              </p>

              {mintedNFTs.length > 0 ? (
                <div className="space-y-6">
                  {/* NFT Gallery/List */}
                  {mintedNFTs.length > 1 && (
                    <div className="mb-4">
                      <h3 className="text-lg font-medium text-gray-800 mb-3">My NFT Collection</h3>
                      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                        {mintedNFTs.map((nft, index) => (
                          <div 
                            key={`${nft.contractAddress}-${nft.tokenId}`}
                            className="cursor-pointer border rounded-lg p-2 hover:shadow-md transition-shadow"
                            onClick={() => setViewingNFT({ contractAddress: nft.contractAddress, tokenId: nft.tokenId })}
                          >
                            <div className="text-center">
                              <div className="font-medium">NFT #{nft.tokenId}</div>
                              <div className="text-xs text-gray-500 truncate">
                                {nft.contractAddress.substring(0, 8)}...
                              </div>
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* NFT Viewer */}
                  {viewingNFT && (
                    <div>
                      <h3 className="text-lg font-medium text-gray-800 mb-3">NFT Details</h3>
                      <NFTViewer 
                        contractAddress={viewingNFT.contractAddress} 
                        tokenId={viewingNFT.tokenId} 
                      />
                    </div>
                  )}
                </div>
              ) : (
                <div className="text-center p-8 border border-dashed border-gray-300 rounded-lg">
                  <p className="text-gray-500">
                    You haven't minted any NFTs yet. Create metadata and mint an NFT first.
                  </p>
                  <button
                    onClick={() => setSelectedIndex(1)}
                    className="mt-4 inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700"
                  >
                    Create NFT
                  </button>
                </div>
              )}
            </Tab.Panel>
          </Tab.Panels>
        </Tab.Group>
      </div>

      {/* Add SimpleUploader for testing */}
      <div className="mt-8 border-t pt-8">
        <h2 className="text-xl font-bold mb-4">Test Direct Upload</h2>
        <SimpleUploader />
      </div>
    </div>
  );
};

export default NFTStudio;
