// src/components/PinataSettings.tsx
import React, { useState, useEffect } from 'react';
import { savePinataKeys, getPinataKeys } from '../services/nft';

const PinataSettings: React.FC = () => {
  const [apiKey, setApiKey] = useState(getPinataKeys().apiKey);
  const [secretKey, setSecretKey] = useState(getPinataKeys().secretKey);
  const [saved, setSaved] = useState(false);

  useEffect(() => {
    // Set the default API key you provided
    if (!apiKey) {
      setApiKey('d44af853da8ffac7868c');
    }
  }, [apiKey]);

  const handleSave = () => {
    savePinataKeys(apiKey, secretKey);
    setSaved(true);
    setTimeout(() => setSaved(false), 3000);
  };

  return (
    <div className="bg-white shadow sm:rounded-lg mb-6">
      <div className="px-4 py-5 sm:p-6">
        <h3 className="text-lg leading-6 font-medium text-gray-900">
          Pinata API Settings
        </h3>
        <div className="mt-2 max-w-xl text-sm text-gray-500">
          <p>Configure your Pinata API credentials for image and metadata storage.</p>
        </div>
        <div className="mt-5 space-y-4">
          <div>
            <label htmlFor="api-key" className="block text-sm font-medium text-gray-700">
              API Key
            </label>
            <input
              type="text"
              id="api-key"
              value={apiKey}
              onChange={(e) => setApiKey(e.target.value)}
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            />
          </div>
          <div>
            <label htmlFor="secret-key" className="block text-sm font-medium text-gray-700">
              Secret Key
            </label>
            <input
              type="password"
              id="secret-key"
              value={secretKey}
              onChange={(e) => setSecretKey(e.target.value)}
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
              placeholder="Enter your Pinata Secret Key"
            />
          </div>
          <button
            type="button"
            onClick={handleSave}
            className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
          >
            Save Credentials
          </button>
          {saved && (
            <p className="text-sm text-green-600">Credentials saved successfully!</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default PinataSettings;
